var map_geo;$(document).ready(function(){map_geo=new GMaps({el:"#geo_location",lat:-12.043333,lng:-77.028333}),$("#geocoding_form").submit(function(a){a.preventDefault(),GMaps.geocode({address:$("#address").val().trim(),callback:function(a,e){if("OK"==e){var o=a[0].geometry.location;map_geo.setCenter(o.lat(),o.lng()),map_geo.addMarker({lat:o.lat(),lng:o.lng()})}}})})});